import React, { useContext } from 'react';
import { CartContext } from './CartContext';

const Cart = () => {
  const { cart, totalCost } = useContext(CartContext);

  return (
    <div style={cartContainerStyle}>
      <h2 style={headingStyle}>Your Cart</h2>
      {cart.length === 0 ? (
        <p style={emptyMessageStyle}>Your cart is empty.</p>
      ) : (
        cart.map((item, index) => (
          <div key={index} style={itemStyle}>
            <p><strong>Event:</strong> {item.description}</p>
            <p><strong>Quantity:</strong> {item.quantity}</p>
            <p><strong>Price:</strong> ${item.price.toFixed(2)}</p>
            <p><strong>Subtotal:</strong> ${(item.price * item.quantity).toFixed(2)}</p>
          </div>
        ))
      )}
      <div style={totalCostStyle}>
        <strong>Total Cost:</strong> ${totalCost.toFixed(2)}
      </div>
    </div>
  );
};

// Styling objects
const cartContainerStyle = {
  padding: '30px',
  margin: '40px auto',
  maxWidth: '600px',
  backgroundColor: '#ffffff',
  borderRadius: '15px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.15)',
  border: '1px solid #e0e0e0',
};

const headingStyle = {
  textAlign: 'center',
  color: '#444',
  marginBottom: '25px',
  fontSize: '1.8rem',
};

const itemStyle = {
  padding: '15px',
  borderBottom: '2px solid #f0f0f0',
  marginBottom: '15px',
  backgroundColor: '#fafafa',
  borderRadius: '8px',
  boxShadow: 'inset 0 1px 3px rgba(0, 0, 0, 0.05)',
};

const emptyMessageStyle = {
  textAlign: 'center',
  color: '#888',
  fontSize: '1.2rem',
};

const totalCostStyle = {
  marginTop: '30px',
  textAlign: 'right',
  fontWeight: 'bold',
  fontSize: '1.4em',
  color: '#333',
  borderTop: '2px dashed #ddd',
  paddingTop: '15px',
};

export default Cart;

export { cartContainerStyle, headingStyle, itemStyle, emptyMessageStyle, totalCostStyle };
