import React, { useContext } from 'react';
import { OrdersContext } from './OrdersContext'; // Ensure the path is correct

const YourOrderPage = () => {
  const { orders } = useContext(OrdersContext);

  // Adjust styles to make the page layout similar to EventsPage
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '0 auto',
    maxWidth: '1500px', // Match your EventsPage maxWidth for consistency
  };

  const imageStyle = {
    width: '100%', // Ensure image takes full width
    maxHeight: '600px',
    objectFit: 'cover', // Adjust how the image fits within its box, if necessary
    margin: 0, // Removes any default margin
    position: 'absolute', // Positions the image absolutely relative to its nearest positioned ancestor
    top: 0, // Aligns the top edge of the image with the top edge of its container
    left: 0, // Aligns the left edge of the image with the left edge of its container
    zIndex: -1, // Ensures the image stays behind other content
  };

  const orderDetailsStyle = {
    backgroundColor: '#f3f3f3', // Similar background as EventsPage for consistency
    padding: '20px',
    margin: '20px 0', // Provide some spacing between orders or from the top
    boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)', // Similar shadow as event cards for consistency
    width: '90%', // Adjust based on your layout preferences
    borderRadius: '10px', // Match your EventsPage card style
  };

  return (
    <div>
      <img src="/Home12.png" alt="Home" style={imageStyle} />
      <div style={containerStyle}>
        {orders.length > 0 ? (
          orders.map((order, index) => (
            <div key={index} style={orderDetailsStyle}>
              <h3>Order ID: {order.ticketId}</h3>
              <p>Total Cost: ${order.totalCost.toFixed(2)}</p>
              <p>Customer: {order.customerDetails.firstName} {order.customerDetails.lastName}</p>
              <p>Address: {order.customerDetails.address}</p>
              <ul>
                {order.items.map((item, itemIndex) => (
                  <li key={itemIndex}>{item.description} - Quantity: {item.quantity}</li>
                ))}
              </ul>
            </div>
          ))
        ) : (
          <div style={orderDetailsStyle}>
            <p>No orders placed.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default YourOrderPage;
