import React, { createContext, useState, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const addToCart = (newItem) => {
    // Ensure newItem has a MongoDB _id
    if (!newItem || typeof newItem._id === 'undefined') {
      console.error("Attempted to add an item without a valid MongoDB _id:", newItem);
      return; // Do not add the item if _id is missing or undefined
    }

    const existingItemIndex = cart.findIndex(item => item._id === newItem._id);
    if (existingItemIndex > -1) {
      // Item exists, update the quantity
      const updatedCart = cart.map((item, index) => {
        if (index === existingItemIndex) {
          return { ...item, quantity: item.quantity + newItem.quantity || 1 };
        }
        return item;
      });
      setCart(updatedCart);
    } else {
      // Item does not exist, add new item with MongoDB _id
      setCart([...cart, { ...newItem, quantity: newItem.quantity || 1 }]);
    }
  };

  const removeFromCart = (_id) => {
    if (typeof _id === 'undefined') {
      console.error("Attempted to remove an item without specifying an _id");
      return; // Do not attempt to remove if _id is undefined
    }

    const existingItemIndex = cart.findIndex(item => item._id === _id);
    if (existingItemIndex > -1) {
      // Found the item, now decide to decrement or remove
      const updatedCart = cart[existingItemIndex].quantity > 1 
        ? cart.map(item => item._id === _id ? { ...item, quantity: item.quantity - 1 } : item)
        : cart.filter(item => item._id !== _id); // Remove the item if quantity is 1 or less
      setCart(updatedCart);
    }
  };


  useEffect(() => {
    // Calculate and update total cost whenever the cart changes
    const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotalCost(total);
  }, [cart]);

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, totalCost }}>
      {children}
    </CartContext.Provider>
  );
};
