import React, { useState, useEffect } from 'react';

const BlogPage = () => {
  const [pastEvents, setPastEvents] = useState([]);
  const [reviews, setReviews] = useState({});

  useEffect(() => {
    // Fetch past events
    fetch('/api/past-events')
      .then(response => response.json())
      .then(data => {
        setPastEvents(data);
        // For each event, fetch its reviews
        data.forEach(event => {
          fetch(`/api/reviews/${event.id}`)
            .then(response => response.json())
            .then(reviewsData => {
              setReviews(prevReviews => ({
                ...prevReviews,
                [event.id]: reviewsData
              }));
            })
            .catch(error => console.error('Error fetching reviews:', error));
        });
      })
      .catch(error => console.error('Error fetching past events:', error));
  }, []);

  return (
    <div>
      <img src="/blog.png" alt="Blog" style={{ width: '100%', maxHeight: '600px', position: 'absolute', top: 0, left: 0, zIndex: -1 }} />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '0 auto', maxWidth: '1500px', marginTop: '650px' }}>
        {pastEvents.map(event => (
          <div key={event.id} style={{ backgroundColor: '#f3f3f3', padding: '20px', margin: '20px 0', boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)', width: '90%', borderRadius: '10px' }}>
            <h3>{event.title}</h3>
            <p>{event.description}</p>
            <img src={event.image} alt={event.title} style={{ width: '100%', height: 'auto', borderRadius: '10px' }} />
            <h4>Reviews</h4>
            {reviews[event.id] && reviews[event.id].length > 0 ? (
              <ul>
                {reviews[event.id].map(review => (
                  <li key={review.userName} style={{ listStyleType: 'none', margin: '10px 0' }}>
                    <strong>{review.userName}:</strong> {review.comment}
                    <div>
                      <img src={review.image} alt="Review" style={{ width: '50px', height: 'auto', marginTop: '10px' }} />
                    </div>
                    <p>{review.description}</p>
                    <p>Rating: {review.rating} / 5</p>
                  </li>
                ))}
              </ul>
            ) : <p>No reviews yet.</p>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogPage;
