const stripe = require('stripe')(process.env.sk_test_51ObW35JuImlUjwClMcXxCRaL1p4Dz7H3BL7ySfC67MWmPjY0vEwmqjeqhfnjqrwI2qV6Q7Xi2PVFXN7uCluFHPu000vLRfrBXf);
const { v4: uuidv4 } = require('uuid');

// Function to generate a reservation ID
const generateReservationId = () => {
  return 'res-' + uuidv4();
};

// Function to create a payment intent
const createPaymentIntent = async (stripe, amount) => { // Pass 'stripe' as a parameter
  console.log(`Creating Payment Intent with Amount: ${amount}`);
  try {
    const paymentIntent = await stripe.paymentIntents.create({
      amount,
      currency: 'usd',
    });
    console.log(`Payment Intent Created: ${paymentIntent.id}`);
    return paymentIntent.client_secret;
  } catch (error) {
    console.error('Error creating payment intent:', error);
    throw error;
  }
};

// Function to get reservation details
const getReservationDetails = (cart, totalCost) => {
  const reservationDetails = {
    reservationId: generateReservationId(),
    items: cart,
    total: totalCost,
    user: 'currentUser',
  };
  console.log('Generated Reservation Details:', reservationDetails);
  return reservationDetails;
};

// Function to handle proceeding to payment
const handleProceedToPayment = async (stripe, cart, navigate) => { // Pass 'stripe' and 'navigate' as parameters
  try {
    // Example: sending the first item's price for the payment
    const response = await fetch('/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ eventId: cart[0].eventId, amount: cart[0].price }),
    });

    const { clientSecret } = await response.json();

    if (clientSecret) {
      navigate('/payment', { state: { clientSecret } });
    } else {
      throw new Error('Payment Intent failed');
    }
  } catch (error) {
    console.error('Error processing payment:', error);
  }
};

module.exports = {
  getReservationDetails,
  handleProceedToPayment,
  generateReservationId,
  createPaymentIntent,
};
