import React, { useContext } from 'react';
import { OrdersContext } from './OrdersContext'; // Ensure the path is correct

const YourOrderPage = () => {
  const { orders } = useContext(OrdersContext);

  const pageStyle = {
    paddingTop: '100px', // Adjust this to account for your navbar's height
    textAlign: 'center',
  };

  const orderDetailsStyle = {
    marginTop: '20px', // Ensure there's space between orders
    padding: '20px',
  };

  return (
    <div style={pageStyle}>
      <h2>Your Orders</h2>
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={index} style={orderDetailsStyle}>
            <h3>Order ID: {order.ticketId}</h3>
            <p>Total Cost: ${order.totalCost.toFixed(2)}</p>
            <p>Customer: {order.customerDetails.firstName} {order.customerDetails.lastName}</p>
            <p>Address: {order.customerDetails.address}</p>
            <ul>
              {order.items.map((item, itemIndex) => (
                <li key={itemIndex}>{item.description} - Quantity: {item.quantity}</li>
              ))}
            </ul>
          </div>
        ))
      ) : (
        <p>No orders placed.</p>
      )}
    </div>
  );
};

export default YourOrderPage;
