export const CartProvider = ({ children }) => {
    const [cart, setCart] = useState([]);
    const [totalCost, setTotalCost] = useState(0);

    const addToCart = (item) => {
      const existingIndex = cart.findIndex((cartItem) => cartItem.eventId === item.eventId);

      if (existingIndex > -1) {
        // Update existing item quantity
        const newCart = cart.map((cartItem, index) =>
          index === existingIndex ? { ...cartItem, quantity: cartItem.quantity + item.quantity } : cartItem
        );
        setCart(newCart);
      } else {
        // Add new item to cart
        setCart([...cart, item]);
      }

      // Update total cost
      updateTotalCost();
    };

    const updateTotalCost = () => {
      const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
      setTotalCost(total);
    };

    useEffect(() => {
      updateTotalCost();
    }, [cart]);

    return <CartContext.Provider value={{ cart, addToCart, totalCost }}>{children}</CartContext.Provider>;
  };
