import React, { createContext, useState, useEffect } from 'react'; // Corrected imports

export const CartContext = createContext(); // Ensure this is correctly exported

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const addToCart = (item) => {
    // Implement logic to add the item to the cart
    const updatedCart = [...cart, item]; // Simplified example, adjust as needed
    setCart(updatedCart);
    // Optionally update totalCost here or in an effect
  };

  const removeFromCart = (eventId) => {
    const updatedCart = cart.filter(item => item.eventId !== eventId);
    setCart(updatedCart);
    // Recalculate the total cost if necessary
  };


  useEffect(() => {
    // Example: Update totalCost whenever cart changes
    const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotalCost(total);
  }, [cart]);

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, totalCost }}>
  {children}
</CartContext.Provider>

  );
};
