import React, { useContext, useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import eventsData from '../data.js';
import { useNavigate } from 'react-router-dom'; // Replace useHistory with useNavigate
import { CartContext } from './CartContext';

const EventsPage = () => {
  const [events, setEvents] = useState(eventsData.events || []);
  const [isCartVisible, setIsCartVisible] = useState(false);
  const { cart, addToCart, removeFromCart, totalCost } = useContext(CartContext);
  const { loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  useEffect(() => {}, []);

  const handleAddToCart = (event) => {
    // Check if the item is already in the cart
    const existingItem = cart.find(item => item.eventId === event.id);
    if (existingItem) {
      // If exists, increase its quantity
      const updatedCart = cart.map(item => {
        if (item.eventId === event.id) {
          return { ...item, quantity: item.quantity + 1 };
        }
        return item;
      });
      addToCart(updatedCart, true); // Pass the entire cart and a flag indicating an update
    } else {
      // If not, add as a new item
      const newItem = {
        eventId: event.id,
        description: event.title,
        quantity: 1,
        price: event.price,
      };
      addToCart(newItem);
    }
    setIsCartVisible(true);
  };

  const handleShowPayment = () => {
    // Instead of showing the payment form, redirect to the payment page
    history.push('/payment');
  };

  // Cart Summary
  const CartSummary = () => (
    <div style={cartStyle}>
      <button onClick={() => setIsCartVisible(false)} style={closeCartButtonStyle}>X</button>
      <h3>Cart Summary</h3>
      {cart.map((item, index) => (
        <div key={index} style={cartItemStyle}>
          <p>{item.description} - {item.quantity} x ${item.price.toFixed(2)}</p>
          <button onClick={() => removeFromCart(item.eventId)} style={removeItemButtonStyle}>-</button>
        </div>
      ))}
      <p><strong>Total: ${totalCost.toFixed(2)}</strong></p>
      <button onClick={handleShowPayment}>Pay Now</button>
    </div>
  );


  return (
    <div style={pageStyle}>
    <img src="/home.png" alt="Home" style={imageStyle} />
      {!isAuthenticated ? (
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
      )}

      <div style={eventsBoxStyle}>
  {events.map((event) => (
    <div key={event.id} style={eventStyle}>
      <h3>{event.title}</h3>
      <p>Price: ${event.price}</p>
      {isAuthenticated ? (
        <button onClick={() => handleAddToCart(event)}>Add to Cart</button>
      ) : (
        <p style={loginPromptStyle} onClick={loginWithRedirect}>Login to purchase tickets</p>
      )}
    </div>
  ))}
</div>

      {/* Cart Summary */}
      {isCartVisible && <CartSummary />}

      {/* Stripe Payment Form - Hidden initially */}
      {isPaymentVisible && (
        <div style={paymentFormStyle}>
          <form onSubmit={handlePaymentSubmission}>
            <CardElement />
            <button type="submit" disabled={!stripe}>Submit Payment</button>
          </form>
        </div>
      )}
    </div>
  );
};



// Style objects
const modalStyle = {
  position: 'fixed', 
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  zIndex: 1000, 
  backgroundColor: '#fff',
  padding: '20px',
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  borderRadius: '8px',
  width: '80%',
  maxWidth: '400px', 
};

const cartStyle = {
  position: 'fixed', // Position it fixed relative to the viewport
  top: 0, // Start from the top of the viewport
  right: 0, // Align to the right side of the viewport
  width: '20%', // Give it a width (adjust as needed)
  height: '100%', // Full height
  backgroundColor: '#f9f9f9', // Background color
  padding: '20px', // Padding inside the cart
  boxShadow: '-2px 0 5px rgba(0,0,0,0.5)', // Optional shadow for better visibility
  overflowY: 'auto', // Enable scrolling for overflow content
  zIndex: 1001, // Ensure it's above other content
};

const paymentFormStyle = { /* Styles for the payment form */ };

const closeCartButtonStyle = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  cursor: 'pointer',
  border: 'none',
  background: 'transparent',
  fontSize: '1.2rem',
};

const cartItemStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '10px',
};

const loginPromptStyle = {
  color: '#007bff',
  cursor: 'pointer',
  textDecoration: 'underline',
};

const removeItemButtonStyle = {
  marginLeft: '10px',
  cursor: 'pointer',
  border: 'none',
  background: 'transparent',
  color: 'red',
  fontWeight: 'bold',
};


const backdropStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 999, 
};

const pageStyle = {
  position: 'relative',
  paddingTop: '38.8%',
};

const imageStyle = {
  position: 'absolute',
  top: -140,
  left: 0,
  width: '100%',
  height: '34.9%',
};

const eventsBoxStyle = {
  padding: '20px',
  borderRadius: '15px',
  backgroundColor: '#f3f3f3',
  boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
  width: '97%',
  maxWidth: '1500px',
  marginTop: '-100px',
  overflowY: 'auto',
  zIndex: 2,
};

const eventStyle = {
  margin: '48px 0',
  padding: '20px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  backgroundColor: '#fff',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
};

const titleStyle = {
  fontSize: '1.5rem',
  margin: '0 0 10px 0',
  color: '#333',
};

const dateStyle = {
  fontSize: '1.2rem',
  margin: '0 0 10px 0',
  color: '#555',
};

const loginButtonStyle = {
  position: 'absolute',
  top: '290px',
  right: '900px',
  backgroundColor: 'red',
  color: 'white',
  border: 'none',
  boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  padding: '20px 40px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '1rem',
  fontWeight: 'bold',
  textAlign: 'center',
  display: 'inline-block',
  transition: 'background-color 0.3s ease',
};

export default EventsPage;
