import React, { useContext, useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import eventsData from '../data.js';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext';

const EventsPage = () => {
  const [events, setEvents] = useState(eventsData.events || []);
  const [isCartVisible, setIsCartVisible] = useState(false);
  const [isPaymentVisible, setIsPaymentVisible] = useState(false);
  const { cart, addToCart, totalCost } = useContext(CartContext);
  const { loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const stripe = useStripe();
  const elements = useElements();

  useEffect(() => {
    // Initially hide payment form
    setIsPaymentVisible(false);
  }, []);

  const handleAddToCart = (event) => {
    const newItem = {
      eventId: event.id,
      description: event.title,
      quantity: 1, // This example adds one ticket at a time
      price: event.price,
    };
    addToCart(newItem);
    setIsCartVisible(true); // Show the cart
  };

  const handleShowPayment = () => {
    setIsPaymentVisible(true); // Show the payment form
  };

  const handlePaymentSubmission = async (e) => {
    e.preventDefault();

    // Assuming you have a payment processing function
    // Here you would use stripe and elements to process the payment
    console.log('Processing payment...');
  };

  // Cart Summary
  const CartSummary = () => (
    <div style={cartStyle}>
      <button onClick={() => setIsCartVisible(false)} style={closeCartButtonStyle}>X</button>
      <h3>Cart Summary</h3>
      {cart.map((item, index) => (
        <div key={index}>
          <p>{item.description} - {item.quantity} x ${item.price.toFixed(2)}</p>
        </div>
      ))}
      <p><strong>Total: ${totalCost.toFixed(2)}</strong></p>
      <button onClick={handleShowPayment}>Pay Now</button>
    </div>
  );

  return (
    <div style={pageStyle}>
    <img src="/home.png" alt="Home" style={imageStyle} />
      {!isAuthenticated ? (
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
      )}

      <div style={eventsBoxStyle}>
        {events.map((event) => (
          <div key={event.id} style={eventStyle}>
            <h3>{event.title}</h3>
            <p>Price: ${event.price}</p>
            <button onClick={() => handleAddToCart(event)}>Add to Cart</button>
          </div>
        ))}
      </div>

      {/* Cart Summary */}
      {isCartVisible && <CartSummary />}

      {/* Stripe Payment Form - Hidden initially */}
      {isPaymentVisible && (
        <div style={paymentFormStyle}>
          <form onSubmit={handlePaymentSubmission}>
            <CardElement />
            <button type="submit" disabled={!stripe}>Submit Payment</button>
          </form>
        </div>
      )}
    </div>
  );
};



// Style objects
const modalStyle = {
  position: 'fixed', 
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  zIndex: 1000, 
  backgroundColor: '#fff',
  padding: '20px',
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  borderRadius: '8px',
  width: '80%',
  maxWidth: '400px', 
};

const cartStyle = {
  position: 'fixed', // Position it fixed relative to the viewport
  top: 0, // Start from the top of the viewport
  right: 0, // Align to the right side of the viewport
  width: '20%', // Give it a width (adjust as needed)
  height: '100%', // Full height
  backgroundColor: '#f9f9f9', // Background color
  padding: '20px', // Padding inside the cart
  boxShadow: '-2px 0 5px rgba(0,0,0,0.5)', // Optional shadow for better visibility
  overflowY: 'auto', // Enable scrolling for overflow content
  zIndex: 1001, // Ensure it's above other content
};

const paymentFormStyle = { /* Styles for the payment form */ };

const backdropStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 999, 
};

const pageStyle = {
  position: 'relative',
  paddingTop: '38.8%',
};

const imageStyle = {
  position: 'absolute',
  top: -140,
  left: 0,
  width: '100%',
  height: '34.9%',
};

const eventsBoxStyle = {
  padding: '20px',
  borderRadius: '15px',
  backgroundColor: '#f3f3f3',
  boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
  width: '97%',
  maxWidth: '1500px',
  marginTop: '-100px',
  overflowY: 'auto',
  zIndex: 2,
};

const eventStyle = {
  margin: '48px 0',
  padding: '20px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  backgroundColor: '#fff',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
};

const titleStyle = {
  fontSize: '1.5rem',
  margin: '0 0 10px 0',
  color: '#333',
};

const dateStyle = {
  fontSize: '1.2rem',
  margin: '0 0 10px 0',
  color: '#555',
};

const loginButtonStyle = {
  position: 'absolute',
  top: '290px',
  right: '900px',
  backgroundColor: 'red',
  color: 'white',
  border: 'none',
  boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  padding: '20px 40px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '1rem',
  fontWeight: 'bold',
  textAlign: 'center',
  display: 'inline-block',
  transition: 'background-color 0.3s ease',
};

export default EventsPage;
