import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const NavBar = () => {
  const [hoverEvents, setHoverEvents] = useState(false);
  const [hoverOrder, setHoverOrder] = useState(false);
  const [hoverBlog, setHoverBlog] = useState(false);
  const [isSticky, setIsSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const shouldBeSticky = window.scrollY > 440;
      setIsSticky(shouldBeSticky);
    };


    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [isSticky]);

  const navBarStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: '#525252',
    padding: '26px 0',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.15)',
    borderWidth: '13px',
    borderColor: 'lightgray',
    borderRadius: '4px',
    padding: '10px 20px', 
    height:'76px',
    width: '100%',
    marginLeft: '1px',
    zIndex: 1001,
    boxSizing: 'border-box',
    border: '1px solid #f2f2f2',
    textAlign: 'center',
    position: isSticky ? 'sticky' : 'relative',
    top: isSticky ? 0 : '450px',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'white',
    fontSize: '21px', // Increased font size
    padding: '10px 20px', 
    borderRadius: '10px',
    transition: 'background-color 0.3s ease, transform 0.5s ease', // Smooth transition for hover effect
    fontFamily: '"Open Sans", sans-serif', // Set the font family to Open Sans
  };

  const linkHoverStyle = {
    backgroundColor: '#a9a9a9', // Color change on hover
    transform: 'scale(0.90)',     height:'30px',
  };




  return (
    <div style={navBarStyle}>
      <Link to="/events"
            style={{ ...linkStyle, ...(hoverEvents ? linkHoverStyle : null) }}
            onMouseEnter={() => setHoverEvents(true)}
            onMouseLeave={() => setHoverEvents(false)}>
        Events
      </Link>
      <Link to="/your-order"
            style={{ ...linkStyle, ...(hoverOrder ? linkHoverStyle : null) }}
            onMouseEnter={() => setHoverOrder(true)}
            onMouseLeave={() => setHoverOrder(false)}>
        Your Order
      </Link>
      <Link to="/blog"
            style={{ ...linkStyle, ...(hoverBlog ? linkHoverStyle : null) }}
            onMouseEnter={() => setHoverBlog(true)}
            onMouseLeave={() => setHoverBlog(false)}>
        Blog
      </Link>
    </div>
  );
};

export default NavBar;
