import React, { useContext, useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from './CartContext';
import { getEvents, handleProceedToPayment } from '../backend/handlers';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { v4 as uuidv4 } from 'uuid';

// Define your style objects here...

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [showStripeForm, setShowStripeForm] = useState(false);
  const { loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const stripe = useStripe();
  const elements = useElements();
  const { cart, addToCart, removeFromCart, totalCost } = useContext(CartContext);
  const navigate = useNavigate();
  useEffect(() => {
    const fetchAndSetEvents = async () => {
      try {
        console.log("Fetching events...");
        const fetchedEvents = await getEvents();
        console.log("Events fetched:", fetchedEvents);
        setEvents(fetchedEvents);
      } catch (error) {
        console.error('Error loading events:', error);
      }
    };

    fetchAndSetEvents();
  }, []);



  const handleAddToCart = (event) => {
    const newItem = {
      eventId: event.id,
      description: event.title,
      quantity: 1,
      price: event.price,
    };
    addToCart(newItem);
  };

  const proceedToPayment = async () => {
    setShowStripeForm(true);
  };

  const handlePaymentSubmission = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      console.log('Stripe has not loaded');
      return;
    }

    // Replace this with your fetch call to your backend to create a payment intent
    const response = await fetch('/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: totalCost * 100 }), // totalCost in cents
    });
    const { clientSecret } = await response.json();

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        // Add additional payment method details if necessary
      },
    });

    if (result.error) {
      console.log('Payment error:', result.error.message);
    } else if (result.paymentIntent.status === 'succeeded') {
      console.log('Payment succeeded!');
      handleProceedToPayment(cart, totalCost, navigate);
      // Reset cart and other states as necessary
    }
  };



  const CartSummary = () => (
    <div style={cartSummaryStyle}>
      <button onClick={() => setIsCartVisible(false)} style={closeCartButtonStyle}>X</button>
      <h3 style={cartSummaryHeadingStyle}>Cart Summary</h3>
      {cart.map((item, index) => (
        <div key={index} style={cartItemSummaryStyle}>
          <p>{item.description} - {item.quantity} x ${item.price.toFixed(2)}</p>
          <button onClick={() => removeFromCart(item.eventId)} style={removeItemButtonStyle}>-</button>
        </div>
      ))}
      <p style={cartSummaryTotalStyle}><strong>Total: ${totalCost.toFixed(2)}</strong></p>
      <button onClick={handleProceedToPayment} style={payNowButtonStyle}>Pay Now</button>
    </div>
  );


  return (
    <div style={pageStyle}>
      <img src="/home.png" alt="Home" style={imageStyle} />
      {!isAuthenticated ? (
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
      )}
      <div style={eventsBoxStyle}>
        {events.map((event) => (
          <div key={event.id} style={eventStyle}>
            <h3>{event.title}</h3>
            <p>Price: ${event.price}</p>
            {isAuthenticated ? (
              <button onClick={() => handleAddToCart(event)}>Add to Cart</button>
            ) : (
              <p style={loginPromptStyle} onClick={loginWithRedirect}>Login to purchase tickets</p>
            )}
          </div>
        ))}
      </div>
      {isCartVisible && <CartSummary />}
    </div>
  );
};



// Style objects


const cartSummaryStyle = {
  position: 'fixed',
  top: 0,
  right: 0,
  width: '300px', // Adjust width as needed
  height: '100vh',
  backgroundColor: '#f8f8f8',
  padding: '20px',
  boxShadow: '-3px 0px 10px rgba(0, 0, 0, 0.2)',
  transition: 'transform 0.3s ease-in-out',
  overflowY: 'auto',
  zIndex: 1001,
};


const cartSummaryHeadingStyle = {
  textAlign: 'center',
  color: '#333',
  marginBottom: '15px',
  fontSize: '1.5rem',
};

const cartItemSummaryStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: '10px',
  borderBottom: '1px solid #eee',
  paddingBottom: '10px',
};

const cartSummaryTotalStyle = {
  textAlign: 'right',
  marginTop: '15px',
  fontWeight: 'bold',
  color: '#333',
};

const payNowButtonStyle = {
  display: 'block',
  width: '100%',
  padding: '10px',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};



const closeCartButtonStyle = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  cursor: 'pointer',
  border: 'none',
  background: 'transparent',
  fontSize: '1.2rem',
};



const loginPromptStyle = {
  color: '#007bff',
  cursor: 'pointer',
  textDecoration: 'underline',
};

const removeItemButtonStyle = {
  marginLeft: '10px',
  cursor: 'pointer',
  border: 'none',
  background: 'transparent',
  color: 'red',
  fontWeight: 'bold',
};




const pageStyle = {
  position: 'relative',
  paddingTop: '38.8%',
};

const imageStyle = {
  position: 'absolute',
  top: -140,
  left: 0,
  width: '100%',
  height: '34.9%',
};

const eventsBoxStyle = {
  padding: '20px',
  borderRadius: '15px',
  backgroundColor: '#f3f3f3',
  boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
  width: '97%',
  maxWidth: '1500px',
  marginTop: '-100px',
  overflowY: 'auto',
  zIndex: 2,
};

const eventStyle = {
  margin: '48px 0',
  padding: '20px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  backgroundColor: '#fff',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
};




export default EventsPage;
