import React, { useContext, useState } from 'react';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext';
import { useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const { totalCost } = useContext(CartContext);
  const [customerDetails, setCustomerDetails] = useState({
    firstName: '',
    lastName: '',
    address: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerDetails({ ...customerDetails, [name]: value });
  };

  const handlePaymentSubmission = async (event) => {
    event.preventDefault();

    // Simulate successful payment submission
    console.log('Simulating payment success...');
    navigate('/your-orders'); // Redirect to the orders page upon "successful payment"
  };

  return (
    <div>
      <h2>Payment Information</h2>
      <form onSubmit={handlePaymentSubmission}>
        <input
          type="text"
          name="firstName"
          value={customerDetails.firstName}
          onChange={handleInputChange}
          placeholder="First Name"
          required
        />
        <input
          type="text"
          name="lastName"
          value={customerDetails.lastName}
          onChange={handleInputChange}
          placeholder="Last Name"
          required
        />
        <input
          type="text"
          name="address"
          value={customerDetails.address}
          onChange={handleInputChange}
          placeholder="Address"
          required
        />
        <CardElement />
        <button type="submit" disabled={!stripe}>Submit Payment</button>
      </form>
    </div>
  );
};

export default PaymentPage;
