import React, { useContext, useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import eventsData from '../data.js';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext'; // Ensure this path matches where your CartContext is defined

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [selectedEventDescription, setSelectedEventDescription] = useState('');
  const [customerDetails, setCustomerDetails] = useState({
    firstName: '',
    lastName: '',
    email: '',
    address: '',
  });
  const [ticketQuantity, setTicketQuantity] = useState(1);
  const [totalCost, setTotalCost] = useState(0);
  const [selectedTickets, setSelectedTickets] = useState([]);
  const { loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const stripe = useStripe();
  const elements = useElements();

  // Use the CartContext
  const { cart, addToCart, totalCost: cartTotalCost } = useContext(CartContext);

  useEffect(() => {
    setEvents(eventsData.events);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerDetails(prevState => ({ ...prevState, [name]: value }));
  };

  const handleBuyTickets = (eventId, price, description) => {
    setSelectedEvent({ eventId, price });
    setSelectedEventDescription(description);
    setShowForm(true);
  };

  const handlePaymentSubmission = async (event) => {
    event.preventDefault();

    if (!selectedEvent || !stripe || !elements || !isAuthenticated) {
      alert('Please complete all steps before submitting.');
      return;
    }

    const cardElement = elements.getElement(CardElement);
    const { eventId, price } = selectedEvent;

    const response = await fetch('/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ eventId, amount: price * ticketQuantity * 100 }),
    });

    const paymentData = await response.json();
    if (paymentData.error) {
      console.log('Payment error:', paymentData.error);
      alert('Payment error: ' + paymentData.error.message);
      return;
    }

    const result = await stripe.confirmCardPayment(paymentData.clientSecret, {
      payment_method: {
        card: cardElement,
        billing_details: {
          name: `${customerDetails.firstName} ${customerDetails.lastName}`,
          email: customerDetails.email,
          address: {
            line1: customerDetails.address,
          },
        },
      },
    });

    if (result.error) {
      console.log('Payment error:', result.error.message);
      alert('Payment failed: ' + result.error.message);
    } else if (result.paymentIntent.status === 'succeeded') {
      console.log('Payment succeeded!');
      setShowForm(false);
      setCustomerDetails({ firstName: '', lastName: '', email: '', address: '' });

      // Add the selected ticket to the cart
      addToCart({
        eventId: selectedEvent.eventId,
        description: selectedEventDescription,
        quantity: ticketQuantity,
        price: selectedEvent.price,
      });

      // Optionally reset the ticket quantity here
      setTicketQuantity(1);
    }
  };

  // Rendering cart summary
  const renderCartSummary = () => (
    <div>
      <h4>Cart Summary:</h4>
      {cart.map((item, index) => (
        <div key={index}>
          <p>Event: {item.description}</p>
          <p>Quantity: {item.quantity}</p>
          <p>Price per ticket: ${item.price}</p>
          <p>Subtotal: ${item.quantity * item.price}</p>
        </div>
      ))}
      <p>Total Cost: ${cartTotalCost}</p>
    </div>
  );




  const eventDescriptionStyle = {
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '10px',
    backgroundColor: '#fff',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
    marginTop: '140px',
  };

  // Function to increase the ticket quantity
  const increaseTicketQuantity = (e) => {
    e.stopPropagation(); // Stop event propagation
    setTicketQuantity(ticketQuantity + 1);
  };


  // Function to decrease the ticket quantity, with a minimum of 1 ticket
  const decreaseTicketQuantity = (e) => {
    e.stopPropagation(); // Stop event propagation
    if (ticketQuantity > 1) {
      setTicketQuantity(ticketQuantity - 1);
    }
  };


  // Function to calculate the total cost based on the ticket price and quantity
  const calculateTotalCost = () => {
    if (selectedEvent) {
      setTotalCost(selectedEvent.price * ticketQuantity);
    }
  };

  useEffect(() => {
    // Calculate the total cost whenever the ticket quantity or selected event changes
    calculateTotalCost();
  }, [ticketQuantity, selectedEvent]);

  const ticketSelectionStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: '20px',
  };

    // Function to format selected tickets for display
    const formatSelectedTickets = () => {
      return selectedTickets.map((ticket, index) => {
        return `${ticket.quantity}x ${ticket.description}`;
      }).join(' + ');
    };

    return (
      <div style={pageStyle}>
        <img src="/home.png" alt="Home" style={imageStyle} />
        {!isAuthenticated ? (
          <button style={loginButtonStyle} onClick={() => loginWithRedirect()}>
            Login
          </button>
        ) : (
          <button
            style={loginButtonStyle}
            onClick={() => logout({ returnTo: 'http://localhost:3005' })}
          >
            Logout
          </button>
        )}
        <div style={eventsBoxStyle}>
          {events.map((event) => (
            <div key={event.id} style={eventStyle}>
              <h3 style={titleStyle}>{event.title}</h3>
              <p style={dateStyle}>
                {event.startDate} - {event.endDate}
              </p>
              <p>Price: ${event.price}</p>
              <button
                onClick={() =>
                  handleBuyTickets(event.id, event.price, event.description) // Pass the event description
                }
              >
                Buy Tickets
              </button>
            </div>
          ))}
        </div>
        {showForm && (
          <div style={backdropStyle} onClick={() => setShowForm(false)}>
            {isAuthenticated ? (
              <form
                onSubmit={handlePaymentSubmission}
                style={modalStyle}
                onClick={(e) => e.stopPropagation()}
              >
                <input
                  type="text"
                  name="firstName"
                  value={customerDetails.firstName}
                  onChange={handleInputChange}
                  placeholder="First Name"
                  required
                />
                <input
                  type="text"
                  name="lastName"
                  value={customerDetails.lastName}
                  onChange={handleInputChange}
                  placeholder="Last Name"
                  required
                />
                <input
                  type="email"
                  name="email"
                  value={customerDetails.email}
                  onChange={handleInputChange}
                  placeholder="Email"
                  required
                />
                <input
                  type="text"
                  name="address"
                  value={customerDetails.address}
                  onChange={handleInputChange}
                  placeholder="Address"
                  required
                />
                <CardElement />
                <button type="submit" disabled={!stripe}>
                  Pay
                </button>
                {/* Add a "Buy Tickets" button within the popup */}
                <button type="button" onClick={handlePaymentSubmission}>
                  Buy Tickets
                </button>
              </form>
            ) : (
              <div style={modalStyle} onClick={(e) => e.stopPropagation()}>
                <p>Please log in to buy tickets!</p>
                <button onClick={() => loginWithRedirect()}>
                  Login Using Auth0
                </button>
              </div>
            )}
            {/* Display the event description */}
            <div style={eventDescriptionStyle}>
              <p>{selectedEventDescription}</p>
            </div>
            {/* Display the ticket selection and total cost */}
            <div style={ticketSelectionStyle}>
              <p>Ticket Quantity: {ticketQuantity}</p>
              <button onClick={(e) => increaseTicketQuantity(e)}>+</button>
              <button onClick={(e) => decreaseTicketQuantity(e)}>-</button>

              <p>Total Cost: ${totalCost}</p>
            </div>
          </div>
        )}
        {selectedTickets.length > 0 && (
          <div style={ticketSelectionStyle}>
            <h4>Selected Tickets:</h4>
            <p>{formatSelectedTickets()}</p> {/* Display formatted selected tickets */}
          </div>
        )}
      </div>
    );
  };







