import React, { useState, useEffect } from 'react';

const YourOrderPage = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:3006/api/orders');
        const data = await response.json();
        setOrders(data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  // Adjust styles to make the page layout similar to EventsPage
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '0 auto',
    maxWidth: '1500px',
    marginTop: '550px',
  };

  const imageStyle = {
    width: '100%',
    maxHeight: '600px',
    position: 'absolute',
    top: 0,
    left: 0,
    zIndex: -1,
  };

  const orderDetailsStyle = {
    backgroundColor: '#f3f3f3',
    padding: '20px',
    margin: '20px 0',
    boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
    width: '90%',
    borderRadius: '10px',
  };

  return (
    <div>
      <img src="/Home12.png" alt="Home" style={imageStyle} />
      <div style={containerStyle}>
        {orders.length > 0 ? (
          orders.map((order, index) => (
            <div key={index} style={orderDetailsStyle}>
              <h3>Order ID: {order.ticketId}</h3>
              <p>Total Cost: ${order.totalCost.toFixed(2)}</p>
              <p>Customer: {order.customerDetails.firstName} {order.customerDetails.lastName}</p>
              <p>Address: {order.customerDetails.address}</p>
              <ul>
                {order.items.map((item, itemIndex) => (
                  <li key={itemIndex}>{item.description} - Quantity: {item.quantity}</li>
                ))}
              </ul>
            </div>
          ))
        ) : (
          <div style={orderDetailsStyle}>
            <p>No orders placed.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default YourOrderPage;
