import React, { useContext, useState } from 'react';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext';
import { useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const { totalCost } = useContext(CartContext);
  const [customerDetails, setCustomerDetails] = useState({
    firstName: '',
    lastName: '',
    address: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerDetails({ ...customerDetails, [name]: value });
  };

  const handlePaymentSubmission = async (event) => {
    event.preventDefault();
    console.log('Simulating payment success...');
    navigate('/your-orders'); // Redirect to the orders page upon "successful payment"
  };

  return (
    <div style={containerStyle}>
      <h2 style={headingStyle}>Payment Information</h2>
      <form onSubmit={handlePaymentSubmission} style={formStyle}>
        <div style={inputGroupStyle}>
          <input
            type="text"
            name="firstName"
            value={customerDetails.firstName}
            onChange={handleInputChange}
            placeholder="First Name"
            required
            style={inputStyle}
          />
        </div>
        <div style={inputGroupStyle}>
          <input
            type="text"
            name="lastName"
            value={customerDetails.lastName}
            onChange={handleInputChange}
            placeholder="Last Name"
            required
            style={inputStyle}
          />
        </div>
        <div style={inputGroupStyle}>
          <input
            type="text"
            name="address"
            value={customerDetails.address}
            onChange={handleInputChange}
            placeholder="Address"
            required
            style={inputStyle}
          />
        </div>
        <div style={cardElementStyle}>
          <CardElement />
        </div>
        <button type="submit" disabled={!stripe} style={submitButtonStyle}>Submit Payment</button>
      </form>
    </div>
  );
};

const containerStyle = {
  maxWidth: '400px',
  margin: '0 auto',
  padding: '20px',
  boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
  borderRadius: '8px',
  background: '#fff',
};

const headingStyle = {
  textAlign: 'center',
  color: '#333',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '20px',
};

const inputGroupStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '10px',
};

const inputStyle = {
  padding: '10px',
  borderRadius: '4px',
  border: '1px solid #ccc',
};

const cardElementStyle = {
  padding: '10px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const submitButtonStyle = {
  background: '#007bff',
  color: '#fff',
  padding: '10px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default PaymentPage;
