import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react'; 
import eventsData from '../data.js';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const { loginWithRedirect } = useAuth0(); 
  const stripe = useStripe();
  const elements = useElements();

  useEffect(() => {
    console.log('Imported eventsData:', eventsData);
    if (eventsData && eventsData.events) {
      setEvents(eventsData.events);
    }
  }, []);

  useEffect(() => {
    console.log('Current state of events:', events);
  }, [events]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      console.log('Stripe has not loaded yet.');
      return;
    }

    const card = elements.getElement(CardElement);
    const result = await stripe.createPaymentMethod({
      type: 'card',
      card: card,
    });

    if (result.error) {
      console.log(result.error.message);
    } else {
      console.log(result.paymentMethod.id);
      // Here, you can call your backend to create a payment intent and finalize the payment
    }
  };

  return (
    <div style={pageStyle}>
      <img src="/home.png" alt="Home" style={imageStyle} />
      {/* Position the login button over the image */}
      <button style={loginButtonStyle} onClick={() => loginWithRedirect()}>
        Login 
      </button>
      <div style={eventsBoxStyle}>
        {events.map(event => (
          <div key={event.id} style={eventStyle}>
            <h3 style={titleStyle}>{event.title}</h3>
            <p style={dateStyle}>{event.startDate} - {event.endDate}</p>
          </div>
        ))}
      </div>
      {/* Include the Stripe payment form */}
      <form onSubmit={handleSubmit}>
        <CardElement />
        <button type="submit" disabled={!stripe}>Pay</button>
      </form>
    </div>
  );
};

// Styles omitted for brevity

export default EventsPage;
