import React, { useState, useEffect } from 'react';

const BlogPage = () => {
  const [pastEvents, setPastEvents] = useState([]);
  const [reviews, setReviews] = useState({});
  // Assume all users are guests initially
  const [username, setUsername] = useState(`Guest${Math.floor(Math.random() * 10000)}`);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showUsernamePopup, setShowUsernamePopup] = useState(false);


  useEffect(() => {
    // Example fetch to check if user is authenticated
    // In reality, you'd use Auth0's SDK to check this
    fetch('/api/authenticate') // This endpoint needs to be created to check authentication
      .then(response => response.json())
      .then(({ authenticated }) => {
        setIsAuthenticated(authenticated);
        // Show username popup only if authenticated and no username is set
        if (authenticated && !localStorage.getItem('username')) {
          setShowUsernamePopup(true);
        } else {
          // If already authenticated and username exists, use that username
          const storedUsername = localStorage.getItem('username');
          if (storedUsername) {
            setUsername(storedUsername);
          }
        }
      })
      .catch(error => console.error('Authentication check failed:', error));


    // Fetch past events
    fetch('/api/past-events')
      .then(response => response.json())
      .then(data => {
        setPastEvents(data);
        data.forEach(event => {
          fetch(`/api/reviews/${event.id}`)
            .then(response => response.json())
            .then(reviewsData => {
              setReviews(prevReviews => ({
                ...prevReviews,
                [event.id]: reviewsData
              }));
            })
            .catch(error => console.error('Error fetching reviews:', error));
        });
      })
      .catch(error => console.error('Error fetching past events:', error));

    // Show username popup if authenticated but no username is set
    // This logic will need to be adapted based on how usernames are stored and verified
    if (isAuthenticated) {
      setShowUsernamePopup(true); // For demonstration, always show on mount if authenticated
    }
  }, []);

  const handleSaveUsername = (newUsername) => {
    console.log("Username saved:", newUsername);
    localStorage.setItem('username', newUsername); // Persist username for session/revisits
    setUsername(newUsername); // Update username in state
    setShowUsernamePopup(false); // Hide popup
  };



  return (
    <div>
      <img src="/blog.png" alt="Blog Background" style={{ width: '100%', maxHeight: '600px', position: 'absolute', top: 0, left: 0, zIndex: -1 }} />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '0 auto', maxWidth: '1500px', paddingTop: '650px' }}>
      {pastEvents.map(event => (
  <div key={event.id} style={{ backgroundColor: '#f3f3f3', padding: '20px', margin: '20px 0', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)', width: '90%', borderRadius: '10px', position: 'relative', zIndex: 1 }}>
    <h3>{event.title}</h3>
    <p><strong>Posted by: Spot - Mtl</strong></p> {/* Add this line */}
    <p>{event.description}</p>
    <img src={event.image} alt={event.title} style={{ width: '55%', height: 'auto', borderRadius: '10px', display: 'block', marginLeft: 'auto', marginRight: 'auto' }} />
            <div style={{marginTop: '20px', paddingTop: '15px', borderTop: '1px solid #ccc'}}>
              <h4>Comments</h4>
              {reviews[event.id] && reviews[event.id].length > 0 ? (
                <ul style={{ listStyleType: 'none', padding: '0' }}>
                  {reviews[event.id].map(review => (
                    <li key={review.userName} style={{ background: '#e9e9e9', margin: '10px 0', padding: '10px', borderRadius: '5px' }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '10px' }}>
                        <strong>{review.userName}</strong>
                      </div>
                      <p style={{ margin: '5px 0' }}>{review.comment}</p>
                      <p style={{ fontStyle: 'italic' }}>{review.description}</p>
                      <p>Rating: {review.rating} / 5</p>
                    </li>
                  ))}
                </ul>
              ) : <p>No reviews yet.</p>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogPage;
