import React, { useContext, useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import eventsData from '../data.js';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext';

const EventsPage = () => {
  const [events, setEvents] = useState(eventsData.events || []);
  const [isCartVisible, setIsCartVisible] = useState(false);
  const [isPaymentVisible, setIsPaymentVisible] = useState(false);
  const { cart, addToCart, totalCost } = useContext(CartContext);
  const { loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const stripe = useStripe();
  const elements = useElements();

  useEffect(() => {
    // Initially hide payment form
    setIsPaymentVisible(false);
  }, []);

  const handleAddToCart = (event) => {
    const newItem = {
      eventId: event.id,
      description: event.title,
      quantity: 1, // This example adds one ticket at a time
      price: event.price,
    };
    addToCart(newItem);
    setIsCartVisible(true); // Show the cart
  };

  const handleShowPayment = () => {
    setIsPaymentVisible(true); // Show the payment form
  };

  const handlePaymentSubmission = async (e) => {
    e.preventDefault();

    // Assuming you have a payment processing function
    // Here you would use stripe and elements to process the payment
    console.log('Processing payment...');
  };

  // Cart Summary
  const CartSummary = () => (
    <div style={cartStyle}>
      <h3>Cart Summary</h3>
      {cart.map((item, index) => (
        <div key={index}>
          <p>{item.description} - {item.quantity} x ${item.price.toFixed(2)}</p>
        </div>
      ))}
      <p><strong>Total: ${totalCost.toFixed(2)}</strong></p>
      <button onClick={handleShowPayment}>Pay Now</button>
    </div>
  );

  return (
    <div style={pageStyle}>
      {/* Authentication and Event Listing */}
      {!isAuthenticated ? (
        <button onClick={loginWithRedirect}>Login</button>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
      )}

      <div style={eventsBoxStyle}>
        {events.map((event) => (
          <div key={event.id} style={eventStyle}>
            <h3>{event.title}</h3>
            <p>Price: ${event.price}</p>
            <button onClick={() => handleAddToCart(event)}>Add to Cart</button>
          </div>
        ))}
      </div>

      {/* Cart Summary */}
      {isCartVisible && <CartSummary />}

      {/* Stripe Payment Form - Hidden initially */}
      {isPaymentVisible && (
        <div style={paymentFormStyle}>
          <form onSubmit={handlePaymentSubmission}>
            <CardElement />
            <button type="submit" disabled={!stripe}>Submit Payment</button>
          </form>
        </div>
      )}
    </div>
  );
};
