import React, { useContext } from 'react';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import { CartContext } from './CartContext';
import { useNavigate } from 'react-router-dom'; // Replace useHistory with useNavigate

const PaymentPage = () => {
  const stripe = useStripe();
  const elements = useElements();
  const { totalCost, cart } = useContext(CartContext);
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handlePaymentSubmission = async (event) => {
    event.preventDefault();
    // Implement Stripe payment logic here
    console.log('Processing payment...');

    // Dummy payment success logic
    if (stripe && elements) {
      // Simulate successful payment
      console.log('Payment successful! Redirecting to orders page...');
      navigate('/your-orders'); // Redirect to the orders page upon successful payment
    }
  };

  return (
    <div>
      <h2>Payment Information</h2>
      <form onSubmit={handlePaymentSubmission}>
        <CardElement />
        <button type="submit" disabled={!stripe}>Submit Payment</button>
      </form>
    </div>
  );
};

export default PaymentPage;
