import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react'; 
import eventsData from '../data.js';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const { loginWithRedirect } = useAuth0(); 
  const stripe = useStripe();
  const elements = useElements();

  useEffect(() => {
    console.log('Imported eventsData:', eventsData);
    if (eventsData && eventsData.events) {
      setEvents(eventsData.events);
    }
  }, []);

  useEffect(() => {
    console.log('Current state of events:', events);
  }, [events]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      console.log('Stripe has not loaded yet.');
      return;
    }

    const card = elements.getElement(CardElement);
    const result = await stripe.createPaymentMethod({
      type: 'card',
      card: card,
    });

    if (result.error) {
      console.log(result.error.message);
    } else {
      console.log(result.paymentMethod.id);
      // Here, you can call your backend to create a payment intent and finalize the payment
    }
  };

  return (
    <div style={pageStyle}>
      <img src="/home.png" alt="Home" style={imageStyle} />
      {/* Position the login button over the image */}
      <button style={loginButtonStyle} onClick={() => loginWithRedirect()}>
        Login 
      </button>
      <div style={eventsBoxStyle}>
        {events.map(event => (
          <div key={event.id} style={eventStyle}>
            <h3 style={titleStyle}>{event.title}</h3>
            <p style={dateStyle}>{event.startDate} - {event.endDate}</p>
          </div>
        ))}
      </div>
      {/* Include the Stripe payment form */}
      <form onSubmit={handleSubmit}>
        <CardElement />
        <button type="submit" disabled={!stripe}>Pay</button>
      </form>
    </div>
  );
};


const pageStyle = {
  position: 'relative',
  paddingTop: '38.8%',
};

const imageStyle = {
  position: 'absolute',
  top: -140,
  left: 0,
  width: '100%',
  height: '34.9%',
};

const eventsBoxStyle = {
  padding: '20px',
  borderRadius: '15px',
  backgroundColor: '#f3f3f3',
  boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
  width: '97%',
  maxWidth: '1500px',
  marginTop: '-100px',
  overflowY: 'auto',
  zIndex: 2,
};

const eventStyle = {
  margin: '48px 0',
  padding: '20px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  backgroundColor: '#fff',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
};

const titleStyle = {
  fontSize: '1.5rem',
  margin: '0 0 10px 0',
  color: '#333',
};

const dateStyle = {
  fontSize: '1.2rem',
  margin: '0 0 10px 0',
  color: '#555',
};

const loginButtonStyle = {
    position: 'absolute',
    top: '290px', 
    right: '900px', 
    backgroundColor: 'red', // Gradient background
    color: 'white',
    border: 'none', // Removing the complex border settings
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)', // Simplified shadow for a subtle depth effect
    padding: '20px 40px', // Adjust padding as needed to fit the text
    borderRadius: '5px', // Slightly rounded corners for a modern look
    cursor: 'pointer',
    fontSize: '1rem', // Adjust font size as needed
    fontWeight: 'bold',
    textAlign: 'center', // Center the text within the button
    display: 'inline-block', // Use inline-block for natural width based on content
    transition: 'background-color 0.3s ease', // Smooth transition for hover effect
  };

export default EventsPage;
