import React, { useState, useEffect } from 'react';
import { useAuth } from './AuthContext'; // Ensure this path is correct

const YourOrderPage = () => {
  const [orders, setOrders] = useState(null); // Assuming multiple orders can be returned
  const { isUserAuthenticated, getAccessTokenSilently } = useAuth(); // Use the context to check if the user is authenticated

  useEffect(() => {
    if (!isUserAuthenticated) {
      console.log('User is not authenticated. Skipping fetch.');
      return;
    }

    const fetchUserReservations = async () => {
      try {
        const token = await getAccessTokenSilently();
        // Adjusted to fetch all reservations for the logged-in user
        const response = await fetch('http://localhost:3006/api/reservations', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error('Failed to fetch reservations for the user');
        }
        const data = await response.json();
        if (data && data.length > 0) { // Check if data exists and is not empty
          console.log("User reservations data:", data);
          setOrders(data); // Set the orders state with fetched data
        } else {
          console.error('No reservations found for the user');
        }
      } catch (error) {
        console.error('Error fetching reservations for the user:', error);
      }
    };

    fetchUserReservations();
  }, [isUserAuthenticated, getAccessTokenSilently]); // Re-fetch when these dependencies change

  // Render method updated to handle multiple orders
  return (
    <div>
      <img src="/Home12.png" alt="Home" style={{ width: '100%', maxHeight: '600px', position: 'absolute', top: 0, left: 0, zIndex: -1 }} />
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '0 auto', maxWidth: '1500px', marginTop: '550px' }}>
        {orders && orders.length > 0 ? orders.map((order, index) => (
          <div key={index} style={{ backgroundColor: '#f3f3f3', padding: '20px', margin: '20px 0', boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)', width: '90%', borderRadius: '10px' }}>
            <h3>Order ID: {order.ticketId}</h3>
            <p>Total Cost: ${order.totalCost.toFixed(2)}</p>
            <p>Customer: {order.customerDetails.firstName} {order.customerDetails.lastName}</p>
            <p>Address: {order.customerDetails.address}</p>
            <ul>
              {order.items.map((item, itemIndex) => (
                <li key={itemIndex}>{item.description} - ${item.price} x {item.quantity}</li>
              ))}
            </ul>
          </div>
        )) : (
          <div style={{ backgroundColor: '#f3f3f3', padding: '20px', margin: '20px 0', boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)', width: '90%', borderRadius: '10px' }}>
            <p>No reservations found for the user.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default YourOrderPage;
