import React, { createContext, useState, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  // Function to add an item to the cart
  const addToCart = (item) => {
    const existingItemIndex = cart.findIndex(cartItem => cartItem.eventId === item.eventId);
    if (existingItemIndex > -1) {
      // Update quantity if the item already exists
      const updatedCart = cart.map((cartItem, index) => {
        if (index === existingItemIndex) {
          return {
            ...cartItem,
            quantity: cartItem.quantity + item.quantity,
          };
        }
        return cartItem;
      });
      setCart(updatedCart);
    } else {
      // Add a new item to the cart
      setCart([...cart, item]);
    }
  };

  // Recalculate the total cost whenever the cart changes
  useEffect(() => {
    const newTotalCost = cart.reduce((total, currentItem) => {
      return total + (currentItem.price * currentItem.quantity);
    }, 0);
    setTotalCost(newTotalCost);
  }, [cart]);

  return (
    <CartContext.Provider value={{ cart, totalCost, addToCart }}>
      {children}
    </CartContext.Provider>
  );
};
