import React, { createContext, useState, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const addToCart = (newItem) => {
    // Ensure newItem has a valid id
    if (!newItem || typeof newItem.id === 'undefined') {
      console.error("Attempted to add an item without a valid id:", newItem);
      return; // Do not add the item if id is missing or undefined
    }

    const existingItemIndex = cart.findIndex(item => item.id === newItem.id);
    if (existingItemIndex > -1) {
      // Item exists, update the quantity
      const updatedCart = cart.map((item, index) => {
        if (index === existingItemIndex) {
          return { ...item, quantity: item.quantity + newItem.quantity || 1 };
        }
        return item;
      });
      setCart(updatedCart);
    } else {
      // Item does not exist, add new item
      setCart([...cart, { ...newItem, quantity: newItem.quantity || 1 }]);
    }
  };

  const removeFromCart = async (id) => {
    if (typeof id === 'undefined') {
      console.error("Attempted to remove an item without specifying an id");
      return; // Do not attempt to remove if id is undefined
    }

    const existingItemIndex = cart.findIndex(item => item.id === id);
    if (existingItemIndex > -1) {
      // Found the item, now decide to decrement or remove
      const updatedCart = cart[existingItemIndex].quantity > 1 
        ? cart.map(item => item.id === id ? { ...item, quantity: item.quantity - 1 } : item)
        : cart.filter(item => item.id !== id); // Remove the item if quantity is 1 or less
      setCart(updatedCart);

      // Make a request to update the quantity in the backend
      try {
        const response = await fetch(`http://localhost:3006/api/events/${id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ quantity: -1 }), // Assuming you want to decrement quantity by 1
        });
        if (!response.ok) {
          throw new Error('Failed to update quantity in the backend');
        }
      } catch (error) {
        console.error('Error updating quantity in the backend:', error);
        // You might want to handle the error appropriately, e.g., show a message to the user
      }
    }
  };

  useEffect(() => {
    // Calculate and update total cost whenever the cart changes
    const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotalCost(total);
  }, [cart]);

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, totalCost }}>
      {children}
    </CartContext.Provider>
  );
};
