import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react'; // Import the useAuth0 hook
import eventsData from '../data.js';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const { loginWithRedirect } = useAuth0(); 

  useEffect(() => {
    console.log('Imported eventsData:', eventsData);
    if (eventsData && eventsData.events) {
      setEvents(eventsData.events);
    }
  }, []);

  useEffect(() => {
    console.log('Current state of events:', events);
  }, [events]);

  return (
    <div style={pageStyle}>
      <img src="/home.png" alt="Home" style={imageStyle} />
      {/* Position the login button over the image */}
      <button style={loginButtonStyle} onClick={() => loginWithRedirect()}>
        Login 
      </button>
      <div style={eventsBoxStyle}>
        {events.map(event => (
          <div key={event.id} style={eventStyle}>
            <h3 style={titleStyle}>{event.title}</h3>
            <p style={dateStyle}>{event.startDate} - {event.endDate}</p>
            {/* Removed login button from each event */}
          </div>
        ))}
      </div>
    </div>
  );
};

const pageStyle = {
  position: 'relative',
  paddingTop: '38.8%',
};

const imageStyle = {
  position: 'absolute',
  top: -140,
  left: 0,
  width: '100%',
  height: '34.9%',
};

const eventsBoxStyle = {
  padding: '20px',
  borderRadius: '15px',
  backgroundColor: '#f3f3f3',
  boxShadow: '0 6px 10px rgba(0, 0, 0, 0.3)',
  width: '97%',
  maxWidth: '1500px',
  marginTop: '-100px',
  overflowY: 'auto',
  zIndex: 2,
};

const eventStyle = {
  margin: '48px 0',
  padding: '20px',
  border: '1px solid #ddd',
  borderRadius: '10px',
  backgroundColor: '#fff',
  boxShadow: '0 2px 5px rgba(0, 0, 0, 0.15)',
};

const titleStyle = {
  fontSize: '1.5rem',
  margin: '0 0 10px 0',
  color: '#333',
};

const dateStyle = {
  fontSize: '1.2rem',
  margin: '0 0 10px 0',
  color: '#555',
};

const loginButtonStyle = {
    position: 'absolute',
    top: '265px', 
    right: '600px', 
    backgroundColor: 'red',
    color: 'white',
    border: '2px solid', 
    borderColor: 'transparent', 
    backgroundImage: 'linear-gradient(white, white), radial-gradient(circle at top left, #ff8c00, #ff0080)', 
    backgroundOrigin: 'border-box',
    backgroundClip: 'content-box, border-box',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '40px 80px', // Increased padding for larger button size
    borderRadius: '8px',
    cursor: 'pointer',
    fontSize: '1.5rem',
    width: '160px', // Adjust or specify a fixed width if preferred
    height: '60px', // Adjust or specify a fixed height if preferred
  };

export default EventsPage;
