const stripe = require('stripe')(process.env.sk_test_51ObW35JuImlUjwClMcXxCRaL1p4Dz7H3BL7ySfC67MWmPjY0vEwmqjeqhfnjqrwI2qV6Q7Xi2PVFXN7uCluFHPu000vLRfrBXf);
const { v4: uuidv4 } = require('uuid'); // Import uuid

// Function to generate a unique reservation ID
const generateReservationId = () => {
  return uuidv4(); // Generates and returns a unique ID
};

const createPaymentIntent = async (eventId, amount) => {
  try {
    const paymentIntent = await stripe.paymentIntents.create({
      amount,
      currency: 'usd',
      // Add any other required Stripe parameters
    });
    return paymentIntent.client_secret;
  } catch (error) {
    console.error('Error creating payment intent:', error);
    throw error;
  }
};

const getEvents = async () => {
  try {
    const response = await fetch('/api/events');
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    return await response.json();
  } catch (error) {
    console.error('Error fetching events:', error);
    throw error;
  }
};

// Function to get reservation details
const getReservationDetails = (cart, totalCost) => {
  return {
    reservationId: generateReservationId(),
    items: cart,
    total: totalCost,
    user: 'currentUser',
    // Add any other necessary details
  };
};

// Function to handle proceeding to payment
const handleProceedToPayment = async (cart, totalCost, navigate) => {
  try {
    const response = await fetch('/reservations', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ items: cart, totalCost: totalCost, user: 'currentUser' }),
    });

    if (!response.ok) {
      throw new Error('Network response was not ok');
    }

    const reservationDetails = await response.json();
    console.log('Reservation Details:', reservationDetails);
    navigate('/my-orders', { state: { reservationId: reservationDetails.reservationId } });
  } catch (error) {
    console.error('Error processing payment:', error);
  }
};

export { getEvents, getReservationDetails, handleProceedToPayment };
