import React, { createContext, useContext, useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const {
      isAuthenticated,
      loginWithRedirect,
      logout,
      user,
      isLoading,
      getAccessTokenSilently, // Get this method from useAuth0
    } = useAuth0();

    useEffect(() => {
      setIsUserAuthenticated(isAuthenticated);
    }, [isAuthenticated]);

    // Include getAccessTokenSilently in the context value
    return (
      <AuthContext.Provider value={{ isUserAuthenticated, loginWithRedirect, logout, user, isLoading, getAccessTokenSilently }}>
        {children}
      </AuthContext.Provider>
    );
  };


export const useAuth = () => useContext(AuthContext);
